import React, { useRef } from 'react';
import './App.css';


const DrumKeys = (props) => {

    const audio = useRef(null);

    const setSource = (e) => {
        audio.current.play();
    }

    return (
        <div className="drum-pad col-md-4" onClick={setSource}>
          <h2>Click on drums to play sounds</h2>
          <h1>{props.letter}</h1>
          <audio ref={audio} id={props.id} src={props.src} id={props.id}></audio>
        </div>
    )
}
export default DrumKeys;
