import React, { useRef } from 'react';

const DrumKeys = (props) => {

    const audio = useRef(null);


    const setSource = (e) => {
        audio.current.play();
        props.handleDisplay(props.id)
    }

    return (
        <div className="DrumKeys" onClick={setSource}>
          <h1>{props.letter}</h1>
          <audio ref={audio} id={props.id} src={props.src} id={props.id}></audio>
        </div>
    )
}
export default DrumKeys;
